custom_css:
/** Push menu onto new line **/ 
#mega-menu-wrap-top #mega-menu-top &gt; li.mega-menu-megamenu &gt; ul.mega-sub-menu &gt; li.mega-menu-item:first-child {
	padding-left: 0;
}
#mega-menu-wrap-top #mega-menu-top &gt; li.mega-menu-megamenu &gt; ul.mega-sub-menu &gt; li.mega-menu-item:last-child {
	padding-right: 0;
}
#tophead .menu-toggle {
    display: none;
}
@media only screen and (max-width: 1000px) {
    #top-nav .menu-inside-wrapper {
        display: block;
    }
}
@media only screen and (max-width: 640px) {
    .top-news {
        width: calc(100% - 52px);
    }
    #top-nav {
        width: unset;
    }
}
#mega-menu-wrap-top #mega-menu-top li.mega-menu-item {
    border-right: 1px solid rgba(255, 255, 255, 0.15);
}
#mega-menu-wrap-top #mega-menu-top li.mega-menu-item:first-child {
    border-left: 1px solid rgba(255, 255, 255, 0.15);
}
